size([],0).size([X|Y],N) :- size(Y,M), N is M+1.take(0,X,[]).take(N,[X|Y],[X|Z]) :-        N > 0,        M is N-1,        take(M,Y,Z).split(X,U,V) :-        size(X,N),        M is N // 2,        take(M,X,U),        append(U,V,X).merge([],X,X).merge(X,[],X).merge([X|Y],[Z|W],[X|V]) :-        X < Z,        merge(Y,[Z|W],V).merge([X|Y],[Z|W],[Z|V]) :-        X >= Z,        merge([X|Y], W,V).mergesort([],[]) :- !.mergesort([X],[X]) :- !.mergesort(X,Y) :-        split(X,U,V),        mergesort(U,W),        mergesort(V,Z),        merge(W,Z,Y).